                  $('input[name="SP_cplbody_bg_pattern"]').val(_pattern);
                  if($(this).hasClass('selected'))
                    return;
                  else{
                    $(".img-pattern").removeClass('selected');
                    $(".img-pattern").removeClass('active');
                    $(this).addClass('selected');
                  }
                });
              });

