                                        if(i> (limit -1)){
                                            $(this).css('display', 'none');
                                        }
                                    });

                                    $(".sp-vermegamenu .more-wrap",$element).click(function(){
                                        var this_more = $(this);
                                        if($(this).hasClass('open')){
                                            $(".sp-vermegamenu .item-1",$element).each(function(i){
                                                if(i>limit-1){
