                                                }
                                            });
                                            $(this).addClass('open');
                                            $(this_more).html('<span class="more-view">Close Menu</span>');
                                        }
                                    });

                                    var wd_width = $(window).width();
                                    if(wd_width > 767){
                                        offtogglevermegamenu($element);
                                        renderWidthSubmenu($element);
                                    }

                                    $(window).resize(function() {
                                        var sp_width = $( window ).width();
                                        if(sp_width > 767){
                                            offtogglevermegamenu($element);
                                            renderWidthSubmenu($element);
                                        }
                                    });

