                                        renderWidthSubmenu($element);
                                    }

                                    $(window).resize(function() {
                                        var sp_width = $( window ).width();
                                        if(sp_width > 767){
                                            offtogglevermegamenu($element);
                                            renderWidthSubmenu($element);
                                        }
                                    });

